import React, {useState} from 'react'
import AddIcon from '@material-ui/icons/Add';
import Task from '@material-ui/icons/FormatListBulleted';
import Person from '@material-ui/icons/Person';
import Info from '@material-ui/icons/Info';
import SupervisedUserCircleIcon from '@material-ui/icons/SupervisedUserCircle';
import { Avatar, IconButton } from '@material-ui/core';

import './header.css';


function Header() {
    const [checked, setChecked] = useState(true);
    return (
        <nav className="header">
            <div className="header__left">
               <h2 className='header__tag'>Task Management</h2>
            </div>
            <div className="header__right">
                <IconButton className='header__right-button'>
                    <Task className='header__right-icon'/>
                </IconButton>
                <IconButton className='header__right-button' onClick={() => {
                setChecked(!checked);
                if(checked)
                document.getElementsByClassName("info")[0].setAttribute("style", "display: block !important;")
                if(!checked)
                document.getElementsByClassName("info")[0].removeAttribute("style")
            }}>
                <Person className='header__right-icon'/>
            </IconButton>
            <div className="info">
            <button className="info__icon">
                <div className="inix">
                <IconButton>
                < Info/>
             </IconButton>
                </div>
             <strong>Màn Hình / Trợ năng</strong>
            </button>
            <button className="info__icon">
                <div className="inix">
                <IconButton>
                <SupervisedUserCircleIcon  />
             </IconButton>
                </div>
             <strong>Trợ Giúp / Hỗ Trợ</strong>
            </button>
            </div>
            </div>
        </nav>
    )
}

export default Header
